<script>
import skinFront from './skinFront.json'
import {mapMutations, mapGetters} from 'vuex'
import audioAstma from '../assets/astma.mp3'

export default {
    props: {
        level: {type: Number, default: () => 0},
        viewBox: {type: String, default: () => '0 0 321 681'},
        height: {type: String, default: () => '381'},
        width: {type: String, default: () => '303'},
        check: {type: Boolean, default: () => false},
        viewText: {type: Boolean, default: () => false},
        version: {type: String, default: () => "info"},
        show: {type: Boolean, default: () => false},
        showName: {type: Boolean, default: () => false}
    },
    data() {
        return {
            nameCheck: '',
            yes: false,
            skins: skinFront,
            info: ['Выберите последовательность действий, для этого нажмите на кнопку “Действия врача”'],
            array_text: [
                'Жалобы: приступы сухого кашля, затрудненного дыхания, больше затруднен выдох.',
                'Приступы повторяются второй год в мае, так же отмечаются эпизоды отека губ при употреблении в пищу сырых яблок',
                'Семейный анамнез: у отца - бронхиальная астма, поллиноз',
                'Анамнез: сенсибилизация к пыльце березы, в мае - приступы бронхиальной обструкции, эпизоды отека губ при употреблении в пищу сырых яблок',
                'Первая беременность, протекала физиологически, во время беременности заболеваний не отмечалось, мать не курила, алкоголь и наркотических веществ не употребляла',
                'Роды самостоятелные, на сроке 38 недель, осложнений не было, закричал сразу'
            ],
            default_array_text: [
                'Жалобы: приступы сухого кашля, затрудненного дыхания, больше затруднен выдох.',
                'Приступы повторяются второй год в мае, так же отмечаются эпизоды отека губ при употреблении в пищу сырых яблок',
                'Семейный анамнез: у отца - бронхиальная астма, поллиноз',
                'Анамнез: сенсибилизация к пыльце березы, в мае - приступы бронхиальной обструкции, эпизоды отека губ при употреблении в пищу сырых яблок',
                'Первая беременность, протекала физиологически, во время беременности заболеваний не отмечалось, мать не курила, алкоголь и наркотических веществ не употребляла',
                'Роды самостоятелные, на сроке 38 недель, осложнений не было, закричал сразу'
            ],
            astma: new Audio(audioAstma)
        }
    },
    computed: {
        ...mapGetters({
            getArray: 'stor/getArray'
        })
    },
    watch: {
        version(val) {
            if(val === 'info') {
                this.array_text = this.default_array_text
            } else {
                this.array_text = []
            }
            // this.astma.pause();
            // this.astma.currentTime = 0;
        }
    },
    methods: {
        ...mapMutations({
            changeArray: 'stor/changeArray'
        }),
        setJoint(e) {
            // if(this.version == 'a' && e.id == 3) {
            //     this.astma.play()
            // } else {
            //     this.astma.pause();
            //     this.astma.currentTime = 0;
            // }
            this.nameCheck = e.name
            // console.log('setJoint',this.nameCheck)

            if(e[this.version]&&e[this.version].length > 0) {
                this.array_text = e[this.version]
                this.yes = false
            } else {
                this.array_text = []
                this.yes = true
            }

            this.skins.forEach(el => {
                el.class = 'origin'
                el.level2 = false
                el.level3 = false
            })
            
            e.class = e.class == 'origin' ? 'check' : 'origin'

            if(this.level == 2) e.level2 = true
            else if(this.level == 3) e.level3 = true
        }
    }
}
</script>

